INSERT INTO public.blog_posts (title, slug, content, excerpt, status, published_at, tags, featured_image) VALUES
('How AI Supercharges IT Offshoring', 'how-ai-supercharges-it-offshoring', 'Discover how artificial intelligence is revolutionizing offshore development, enhancing productivity, and reducing costs while maintaining quality. In this comprehensive guide, we explore the latest AI tools and techniques being implemented in offshore development teams worldwide.', 'Discover how artificial intelligence is revolutionizing offshore development, enhancing productivity, and reducing costs while maintaining quality.', 'published', '2024-01-15T00:00:00Z', ARRAY['AI', 'Offshoring', 'Productivity'], 'https://images.unsplash.com/photo-1487058792275-0ad4aaf24ca7?w=800&h=400&fit=crop'),
('Case Study: Writeomatic.app', 'case-study-writeomatic-app', 'How we built an AI-powered writing assistant that generates high-quality content, helping businesses scale their content marketing efforts. This case study details our development process, challenges overcome, and the innovative solutions we implemented.', 'How we built an AI-powered writing assistant that generates high-quality content, helping businesses scale their content marketing efforts.', 'published', '2024-01-10T00:00:00Z', ARRAY['Case Study', 'AI', 'Content Marketing'], 'https://images.unsplash.com/photo-1498050108023-c5249f4df085?w=800&h=400&fit=crop'),
('Case Study: Multi-Agent AI System To Create Viral Reels', 'case-study-multi-agent-ai-viral-reels', 'Building an innovative multi-agent AI system that analyzes trends and creates viral-worthy video content for social media platforms. Learn about our approach to AI-driven content creation and the results achieved.', 'Building an innovative multi-agent AI system that analyzes trends and creates viral-worthy video content for social media platforms.', 'published', '2024-01-05T00:00:00Z', ARRAY['AI', 'Video', 'Social Media'], 'https://images.unsplash.com/photo-1531297484001-80022131f5a1?w=800&h=400&fit=crop'),
('The Future of Cloud-Native Development', 'future-cloud-native-development', 'Exploring the latest trends in cloud-native technologies and how they''re shaping the future of software development. From microservices to serverless architectures, discover what''s next in cloud computing.', 'Exploring the latest trends in cloud-native technologies and how they''re shaping the future of software development.', 'published', '2023-12-28T00:00:00Z', ARRAY['Cloud', 'Development', 'Trends'], 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?w=800&h=400&fit=crop'),
('Agile Methodologies in Remote Teams', 'agile-methodologies-remote-teams', 'Best practices for implementing agile methodologies in distributed teams and maintaining productivity across time zones. Learn how to adapt agile principles for remote work environments.', 'Best practices for implementing agile methodologies in distributed teams and maintaining productivity across time zones.', 'published', '2023-12-20T00:00:00Z', ARRAY['Agile', 'Remote Work', 'Management'], 'https://images.unsplash.com/photo-1461749280684-dccba630e2f6?w=800&h=400&fit=crop'),
('Sustainable Tech: Our Climate Initiative', 'sustainable-tech-climate-initiative', 'How Ahdus Technology is contributing to environmental sustainability through green coding practices and carbon-neutral operations. Discover our commitment to responsible technology development.', 'How Ahdus Technology is contributing to environmental sustainability through green coding practices and carbon-neutral operations.', 'published', '2023-12-15T00:00:00Z', ARRAY['Sustainability', 'Environment', 'Green Tech'], 'https://images.unsplash.com/photo-1487058792275-0ad4aaf24ca7?w=800&h=400&fit=crop'),
